PROCEDURE Proc_GetNewEmployeeCode ()
BEGIN
  SET @LASTEST_CODE = (SELECT
      e.EmployeeCode
    FROM Employee e
    ORDER BY e.CreatedDate DESC LIMIT 1);
  SET @SUB_LASTEST_CODE = (SELECT
      Func_GetSubfixIsNumber(@LASTEST_CODE));
